import React, { useState, useEffect } from "react";
import { getAuth, getClaimsByCustomerId } from "../utils/api";

export default function CustomerStatus() {
  const [claims, setClaims] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchClaims = async () => {
    try {
      const auth = getAuth();
      if (!auth) throw new Error("Not logged in");

      const data = await getClaimsByCustomerId(auth.userId);
      setClaims(data);
    } catch (err) {
      setError(err.message || "Failed to fetch claims");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchClaims();

    // ✅ Polling every 5 seconds for real-time updates
    const interval = setInterval(fetchClaims, 5000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <p>⏳ Loading your claims...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ maxWidth: 800, margin: "0 auto" }}>
      <h2>My Claim Status</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Type</th>
            <th>Description</th>
            <th>Amount</th>
            <th>Incident Date</th>
            <th>Status</th>
            <th>File</th>
          </tr>
        </thead>
        <tbody>
          {claims.map((c) => (
            <tr key={c.id}>
              <td>{c.id}</td>
              <td>{c.claimType}</td>
              <td>{c.description}</td>
              <td>{c.claimAmount}</td>
              <td>{c.incidentDate}</td>
              <td>{c.status}</td>
              <td>
                {c.fileUrl ? (
                  <a href={c.fileUrl} target="_blank" rel="noopener noreferrer">
                    View
                  </a>
                ) : (
                  "-"
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
