import React, { useEffect, useState } from "react";
import { getAuth, getMyCustomerStatus } from "../utils/api";  // ✅ FIXED imports
import "./CustomerStatus.css";

export default function CustomerStatus() {
  const [status, setStatus] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const auth = getAuth();
    if (!auth || !auth.customerId) {
      setError("You are not a logged-in customer.");
      setLoading(false);
      return;
    }

    const fetchStatus = async () => {
      try {
        const data = await getMyCustomerStatus(auth.customerId); // ✅ Correct API call
        setStatus(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStatus();
  }, []);

  if (loading) return <div className="loading">Loading your status...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!status) return <div>No status found.</div>;

  return (
    <div className="customer-status-container">
      <h2>My Claim Status</h2>
      <table className="status-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Total Claims</th>
            <th>Approved</th>
            <th>Rejected</th>
            <th>Pending</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{status.name}</td>
            <td>{status.totalClaims}</td>
            <td>{status.approvedClaims}</td>
            <td>{status.rejectedClaims}</td>
            <td>{status.pendingClaims}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}
