import React, { useEffect, useState } from "react";
import "./ClaimList.css";
import {
  getAllClaims,
  getAllCustomers,
  getMyClaims,
  updateClaimStatus,
} from "../utils/api";

export default function ClaimList({ forAgent = false, onView }) {
  const [claims, setClaims] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [filter, setFilter] = useState("ALL");
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [editingClaimId, setEditingClaimId] = useState(null);
  const [editStatus, setEditStatus] = useState("");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const pageSize = 5;

  // -------- Fetch claims on first render --------
  useEffect(() => {
    async function fetchClaims() {
      setLoading(true);
      try {
        if (forAgent) {
          // Admin/Agent sees all claims
          const allClaims = await getAllClaims();
          const allCustomers = await getAllCustomers();
          setClaims(allClaims || []);
          setCustomers(allCustomers || []);
        } else {
          // Customer sees only their claims
          const storedAuth = localStorage.getItem("authData");
          if (!storedAuth) return;

          const auth = JSON.parse(storedAuth);
          if (!auth.customerId) return;

          const myClaims = await getMyClaims(auth.customerId);
          setClaims(myClaims || []);
        }
      } catch (err) {
        console.error("Failed to fetch claims:", err);
        setClaims([]);
      } finally {
        setLoading(false);
      }
    }

    fetchClaims();
  }, [forAgent]);

  // -------- Sorting --------
  const handleSort = (field) => {
    const order = sortField === field && sortOrder === "asc" ? "desc" : "asc";
    setSortField(field);
    setSortOrder(order);
  };

  const sortedClaims = [...claims]
    .filter((c) => (filter === "ALL" ? true : c.status === filter))
    .sort((a, b) => {
      if (!sortField) return 0;
      const valA = a[sortField];
      const valB = b[sortField];
      if (valA < valB) return sortOrder === "asc" ? -1 : 1;
      if (valA > valB) return sortOrder === "asc" ? 1 : -1;
      return 0;
    });

  // -------- Pagination --------
  const paginatedClaims = sortedClaims.slice(
    (page - 1) * pageSize,
    page * pageSize
  );

  // -------- Update claim status (for Agent/Admin) --------
  const saveStatusUpdate = async (claimId) => {
    try {
      await updateClaimStatus(claimId, editStatus);
      setClaims((prev) =>
        prev.map((c) => (c.id === claimId ? { ...c, status: editStatus } : c))
      );
      setEditingClaimId(null);
    } catch (err) {
      console.error("Failed to update claim status:", err);
    }
  };

  const getCustomerName = (claim) => {
    return (
      claim.customer?.name ||
      customers.find((c) => c.id === claim.customerId)?.name ||
      "Unknown"
    );
  };

  if (loading) return <p>Loading claims...</p>;

  return (
    <div className="claim-list">
      <h2>{forAgent ? "Manage Claims" : "My Claims"}</h2>

      {/* Filter */}
      <div className="filter-bar">
        <label>Status:</label>
        <select
          value={filter}
          onChange={(e) => {
            setFilter(e.target.value);
            setPage(1);
          }}
        >
          <option value="ALL">All</option>
          <option value="SUBMITTED">Submitted</option>
          <option value="UNDER_REVIEW">Under Review</option>
          <option value="APPROVED">Approved</option>
          <option value="REJECTED">Rejected</option>
        </select>
      </div>

      {/* Table */}
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => handleSort("id")}>ID</th>
            {forAgent && <th>Customer</th>}
            <th onClick={() => handleSort("claimType")}>Type</th>
            <th onClick={() => handleSort("claimAmount")}>Amount</th>
            <th onClick={() => handleSort("status")}>Status</th>
            <th onClick={() => handleSort("submissionDate")}>Submitted</th>
            <th>File</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {paginatedClaims.length === 0 ? (
            <tr>
              <td colSpan={forAgent ? 8 : 7}>No claims found.</td>
            </tr>
          ) : (
            paginatedClaims.map((c) => (
              <tr key={c.id}>
                <td>{c.id}</td>
                {forAgent && <td>{getCustomerName(c)}</td>}
                <td>{c.claimType}</td>
                <td>{c.claimAmount?.toFixed(2)}</td>
                <td>
                  {editingClaimId === c.id ? (
                    <select
                      value={editStatus}
                      onChange={(e) => setEditStatus(e.target.value)}
                    >
                      <option value="SUBMITTED">SUBMITTED</option>
                      <option value="UNDER_REVIEW">UNDER_REVIEW</option>
                      <option value="APPROVED">APPROVED</option>
                      <option value="REJECTED">REJECTED</option>
                    </select>
                  ) : (
                    c.status
                  )}
                </td>
                <td>{c.submissionDate}</td>
                <td>
                  {c.fileUrl ? (
                    <a href={c.fileUrl} target="_blank" rel="noopener noreferrer">
                      View File
                    </a>
                  ) : (
                    <span className="text-muted">No file</span>
                  )}
                </td>
                <td>
                  {forAgent ? (
                    editingClaimId === c.id ? (
                      <>
                        <button
                          className="btn-small"
                          onClick={() => saveStatusUpdate(c.id)}
                        >
                          Save
                        </button>
                        <button
                          className="btn-small"
                          onClick={() => setEditingClaimId(null)}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="btn-small"
                        onClick={() => {
                          setEditingClaimId(c.id);
                          setEditStatus(c.status);
                        }}
                      >
                        Edit
                      </button>
                    )
                  ) : (
                    <button
                      className="btn-small"
                      onClick={() => onView("details", c.id)}
                    >
                      Details
                    </button>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="pagination">
        <button
          onClick={() => setPage((p) => Math.max(1, p - 1))}
          disabled={page === 1}
        >
          Prev
        </button>
        <span>
          Page {page} of {Math.ceil(sortedClaims.length / pageSize)}
        </span>
        <button
          onClick={() =>
            setPage((p) => (p * pageSize < sortedClaims.length ? p + 1 : p))
          }
          disabled={page * pageSize >= sortedClaims.length}
        >
          Next
        </button>
      </div>
    </div>
  );
}
