import React, { useState, useEffect } from "react";
import { getMyClaims } from "../utils/api";
import "./ClaimList.css";

export default function CustomerStatus() {
  const [claims, setClaims] = useState([]);
  const [filter, setFilter] = useState("ALL");
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [notifications, setNotifications] = useState([]); // New
  const pageSize = 5;

  // Fetch claims
  const fetchClaims = async () => {
    try {
      const data = await getMyClaims();
      // Check for status change to trigger notifications
      data.forEach((claim) => {
        const oldClaim = claims.find((c) => c.id === claim.id);
        if (oldClaim && oldClaim.status !== claim.status) {
          setNotifications((prev) => [
            ...prev,
            `Claim #${claim.id} has been ${claim.status}`,
          ]);
        }
      });
      setClaims(data);
    } catch (err) {
      console.error(err);
      setError("Failed to load claims. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Polling every 5 seconds
  useEffect(() => {
    fetchClaims(); // initial fetch
    const interval = setInterval(fetchClaims, 5000); // poll every 5 sec
    return () => clearInterval(interval);
  }, [claims]); // depend on claims for notification check

  const handleSort = (field) => {
    const order = sortField === field && sortOrder === "asc" ? "desc" : "asc";
    setSortField(field);
    setSortOrder(order);
  };

  const sortedClaims = [...claims]
    .filter((c) => (filter === "ALL" ? true : c.status === filter))
    .sort((a, b) => {
      if (!sortField) return 0;
      const valA = a[sortField];
      const valB = b[sortField];
      if (valA < valB) return sortOrder === "asc" ? -1 : 1;
      if (valA > valB) return sortOrder === "asc" ? 1 : -1;
      return 0;
    });

  const paginatedClaims = sortedClaims.slice(
    (page - 1) * pageSize,
    page * pageSize
  );

  if (loading) return <p>Loading claims...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (claims.length === 0) return <p>No claims found.</p>;

  return (
    <div className="claim-list">
      <h2>My Claims</h2>

      {/* Notifications */}
      {notifications.length > 0 && (
        <div className="notifications">
          {notifications.map((note, idx) => (
            <div key={idx} className="notification">
              {note}
            </div>
          ))}
        </div>
      )}

      {/* Status Filter */}
      <div className="filter-bar">
        <label>Status: </label>
        <select
          value={filter}
          onChange={(e) => {
            setFilter(e.target.value);
            setPage(1); // reset page on filter change
          }}
        >
          <option value="ALL">All</option>
          <option value="SUBMITTED">Submitted</option>
          <option value="UNDER_REVIEW">Under Review</option>
          <option value="APPROVED">Approved</option>
          <option value="REJECTED">Rejected</option>
        </select>
      </div>

      {/* Claims Table */}
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => handleSort("id")}>
              ID {sortField === "id" ? (sortOrder === "asc" ? "↑" : "↓") : ""}
            </th>
            <th>Type</th>
            <th onClick={() => handleSort("claimAmount")}>
              Amount {sortField === "claimAmount" ? (sortOrder === "asc" ? "↑" : "↓") : ""}
            </th>
            <th onClick={() => handleSort("status")}>
              Status {sortField === "status" ? (sortOrder === "asc" ? "↑" : "↓") : ""}
            </th>
            <th onClick={() => handleSort("submissionDate")}>
              Submitted {sortField === "submissionDate" ? (sortOrder === "asc" ? "↑" : "↓") : ""}
            </th>
            <th>File</th>
          </tr>
        </thead>
        <tbody>
          {paginatedClaims.map((c) => (
            <tr key={c.id}>
              <td>{c.id}</td>
              <td>{c.claimType}</td>
              <td>{c.claimAmount?.toFixed(2)}</td>
              <td>{c.status}</td>
              <td>{c.submissionDate}</td>
              <td>
                {c.fileUrl ? (
                  <a href={c.fileUrl} target="_blank" rel="noopener noreferrer">
                    View File
                  </a>
                ) : (
                  <span className="text-muted">No file</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="pagination">
        <button onClick={() => setPage((p) => Math.max(1, p - 1))} disabled={page === 1}>
          Prev
        </button>
        <span>
          Page {page} of {Math.ceil(sortedClaims.length / pageSize)}
        </span>
        <button
          onClick={() =>
            setPage((p) => (p * pageSize < sortedClaims.length ? p + 1 : p))
          }
          disabled={page * pageSize >= sortedClaims.length}
        >
          Next
        </button>
      </div>
    </div>
  );
}
