// src/components/CustomerList.jsx
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import * as CustomerService from "../utils/api";
import "./CustomerList.css";

export default function CustomerList({ onView }) {
  const [customers, setCustomers] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [deleteTarget, setDeleteTarget] = useState(null);
  const [search, setSearch] = useState("");
  const [filterRegistered, setFilterRegistered] = useState(true);

  useEffect(() => {
    loadCustomers();
  }, [filterRegistered]);

  const loadCustomers = async () => {
    setLoading(true);
    try {
      let data = [];
      if (filterRegistered) {
        data = await CustomerService.getRegisteredCustomers();
      } else {
        data = await CustomerService.getAllCustomers();
      }
      setCustomers(data.map(c => ({ ...c, id: c.customerId, phoneNumber: c.phone })));
    } catch (err) {
      setError(err.message || "Failed to fetch customers");
    } finally {
      setLoading(false);
    }
  };

  const confirmDelete = async () => {
    if (!deleteTarget) return;
    try {
      await CustomerService.deleteCustomer(deleteTarget.id);
      setDeleteTarget(null);
      loadCustomers();
    } catch (err) {
      setError(err.message || "Failed to delete customer");
    }
  };

  if (loading)
    return (
      <div className="d-flex justify-content-center align-items-center mt-5">
        <div className="spinner-border text-primary" role="status" />
        <span className="ms-2">Loading customers...</span>
      </div>
    );

  if (!customers.length)
    return (
      <div className="container mt-5">
        <div className="alert alert-info text-center shadow-sm">
          <i className="bi bi-people"></i> No customers found.
        </div>
      </div>
    );

  const filteredCustomers = customers.filter(c =>
    c.policyNumber?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container mt-5 customer-list-container">
      <h3>
        Customer List
      </h3>

      {error && <div className="alert alert-danger">{error}</div>}

      {/* Controls: Toggle + Search */}
      <div className="customer-controls">
        <div className="btn-group">
          <button
            className={`btn ${filterRegistered ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setFilterRegistered(true)}
          >
            Registered Customers
          </button>
          <button
            className={`btn ${!filterRegistered ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setFilterRegistered(false)}
          >
            All Customers
          </button>
        </div>

        <div className="customer-search-container">
          <input
            type="text"
            placeholder="Search by policy number..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>
      </div>

      {/* Customer Cards */}
      <div className="row g-4">
        {filteredCustomers.map(customer => (
          <div key={customer.id} className="col-md-4">
            <div className="card h-100 shadow-sm border-0 customer-card">
              <div className="card-body text-center">
                <div className="customer-avatar">
                  {customer.name.charAt(0).toUpperCase()}
                </div>
                <h5 className="fw-bold">{customer.name}</h5>
                <p className="text-muted mb-1">{customer.email}</p>
                <p className="small text-secondary mb-0">
                  <strong>üìû</strong> {customer.phoneNumber || "-"}
                </p>
                <p className="small text-secondary">
                  <strong>üìù</strong> {customer.policyNumber || "-"}
                </p>
              </div>
              <div className="card-footer bg-white border-0 d-flex justify-content-between">
                <button
                  className="btn btn-outline-primary btn-sm"
                  onClick={() => onView("customer-detail", customer.id)}
                >
                  <i className="bi bi-eye"></i> View
                </button>
                <button
                  className="btn btn-outline-danger btn-sm"
                  onClick={() => setDeleteTarget(customer)}
                >
                  <i className="bi bi-trash"></i> Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Delete Modal */}
      {deleteTarget && (
        <div
          className="modal fade show d-block"
          tabIndex="-1"
          style={{ backgroundColor: "rgba(0,0,0,0.5)" }}
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0 shadow-lg">
              <div className="modal-header bg-danger text-white">
                <h5 className="modal-title">
                  <i className="bi bi-exclamation-triangle-fill me-2"></i> Confirm Delete
                </h5>
                <button
                  type="button"
                  className="btn-close btn-close-white"
                  onClick={() => setDeleteTarget(null)}
                ></button>
              </div>
              <div className="modal-body">
                Are you sure you want to delete <b>{deleteTarget.name}</b>?
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setDeleteTarget(null)}>
                  Cancel
                </button>
                <button className="btn btn-danger" onClick={confirmDelete}>
                  Yes, Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
