import React, { useEffect, useState } from "react";
import axios from "axios";

export default function CustomerStatus() {
  const [status, setStatus] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchStatus = async () => {
      setLoading(true);
      setError("");

      // Get auth info from localStorage
    const auth = JSON.parse(localStorage.getItem("auth"));
if (!auth || !auth.customerId) {
    setError("No customerId found. Please contact support.");
    return;
}


      try {
        const res = await axios.get(
          `http://localhost:8080/api/customers/status/me/${auth.customerId}`
        );
        setStatus(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch customer status.");
      } finally {
        setLoading(false);
      }
    };

    fetchStatus();
  }, []);

  if (loading) return <p>Loading customer status...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="customer-status">
      <h2>Customer Claim Summary</h2>
      {status && (
        <table>
          <tbody>
            <tr>
              <td><strong>Customer Name:</strong></td>
              <td>{status.name}</td>
            </tr>
            <tr>
              <td><strong>Total Claims:</strong></td>
              <td>{status.totalClaims}</td>
            </tr>
            <tr>
              <td><strong>Approved Claims:</strong></td>
              <td>{status.approvedClaims}</td>
            </tr>
            <tr>
              <td><strong>Rejected Claims:</strong></td>
              <td>{status.rejectedClaims}</td>
            </tr>
            <tr>
              <td><strong>Pending Claims:</strong></td>
              <td>{status.pendingClaims}</td>
            </tr>
          </tbody>
        </table>
      )}
    </div>
  );
}
