import React, { useState, useEffect } from "react";
import { getMyClaims, getAuth } from "../utils/api";
import "./ClaimList.css";

export default function CustomerStatus() {
  const [claims, setClaims] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchClaims = async () => {
    setLoading(true);
    setError("");

    try {
      const auth = getAuth();
      if (!auth || !auth.customerId) {
        setError("Failed to retrieve customerId. Please contact support.");
        setClaims([]);
        return;
      }

      const data = await getMyClaims();
      if (data.length === 0) setError("No claims found.");
      setClaims(data);
    } catch (err) {
      console.error(err);
      setError("Failed to load claims. Please try again.");
      setClaims([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchClaims();
  }, []);

  if (loading) return <p>Loading claims...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="claim-list">
      <h2>My Claims</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Status</th>
            <th>Submitted</th>
            <th>File</th>
          </tr>
        </thead>
        <tbody>
          {claims.map((c) => (
            <tr key={c.id}>
              <td>{c.id}</td>
              <td>{c.claimType}</td>
              <td>{c.claimAmount?.toFixed(2)}</td>
              <td>{c.status}</td>
              <td>{new Date(c.submissionDate).toLocaleString()}</td>
              <td>
                {c.fileUrl ? (
                  <a href={c.fileUrl} target="_blank" rel="noopener noreferrer">
                    View
                  </a>
                ) : (
                  <span>No file</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
