import React, { useEffect, useState } from "react";
import { getCustomerStatuses } from "../utils/api"; // new API endpoint
import "./CustomerStatus.css";

export default function CustomerStatus() {
  const [statuses, setStatuses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchStatuses = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getCustomerStatuses(); // call backend
      setStatuses(data);
    } catch (err) {
      setError(err.message || "Failed to load customer statuses");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStatuses();
  }, []);

  if (loading) return <div className="loading">Loading customer statuses...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!statuses.length) return <div>No customer data available.</div>;

  return (
    <div className="customer-status-container">
      <h2>Customer Claim Status</h2>
      <table className="status-table">
        <thead>
          <tr>
            <th>Customer ID</th>
            <th>Name</th>
            <th>Total Claims</th>
            <th>Approved</th>
            <th>Rejected</th>
            <th>Pending</th>
          </tr>
        </thead>
        <tbody>
          {statuses.map((c) => (
            <tr key={c.customerId}>
              <td>{c.customerId}</td>
              <td>{c.name}</td>
              <td>{c.totalClaims}</td>
              <td>{c.approved}</td>
              <td>{c.rejected}</td>
              <td>{c.pending}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
