// src/components/CustomerStatus.js
import React, { useEffect, useState } from "react";
import { getAuth } from "../utils/util";
import { getCustomerById, getClaimsByCustomerId } from "../utils/api";

export default function CustomerStatus() {
  const [customer, setCustomer] = useState(null);
  const [claims, setClaims] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const auth = getAuth();
        if (!auth?.customerId) throw new Error("Not logged in as customer");

        // Fetch customer details
        const customerData = await getCustomerById(auth.customerId);
        setCustomer(customerData);

        // Fetch claims for this customer
        const customerClaims = await getClaimsByCustomerId(auth.customerId);
        setClaims(customerClaims);
      } catch (err) {
        setError(err.message || "Failed to fetch customer data");
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    // ✅ Polling every 5 seconds for live claim status updates
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <p className="text-center mt-5">Loading...</p>;
  if (error) return <p className="text-center mt-5 text-danger">{error}</p>;
  if (!customer) return null;

  return (

    <div className="container mt-5">
      {/* Customer Info */}
      <div className="card shadow-sm p-4 text-center mb-4">
        <div
          style={{
            width: 80,
            height: 80,
            borderRadius: "50%",
            background: "#0d6efd",
            margin: "0 auto 20px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: "#fff",
            fontWeight: "bold",
            fontSize: 28,
          }}
        >
          {customer.name.charAt(0).toUpperCase()}
        </div>
        <h3>{customer.name}</h3>
        <p><strong>Email:</strong> {customer.email}</p>
        <p><strong>Phone:</strong> {customer.phone || "-"}</p>
        <p><strong>Policy:</strong> {customer.policyNumber || "-"}</p>
      </div>

      {/* Customer Claims */}
      <h4>My Claims</h4>
      <div className="list-group">
        {claims.length === 0 && <p>No claims submitted yet.</p>}
        {claims.map((claim) => (
          <div
            key={claim.id}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              claim.status === "APPROVED"
                ? "list-group-item-success"
                : claim.status === "REJECTED"
                ? "list-group-item-danger"
                : ""
            }`}
          >
            <span>Claim #{claim.id}: {claim.description || "-"}</span>
            <span>
              <strong>Status:</strong> {claim.status || "Pending"}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
}
