import React, { useEffect, useState } from "react";
import { getAuth, getUserById } from "../utils/api";

export default function UserProfile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const auth = getAuth();
    if (!auth) {
      setError("‚ùå You must be logged in to view profile.");
      setLoading(false);
      return;
    }

    const fetchUser = async () => {
      try {
        const data = await getUserById(auth.userId);
        setUser(data);
      } catch (err) {
        console.error("Error fetching user:", err);
        setError("‚ùå Failed to fetch user details.");
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading)
    return (
      <p
        style={{
          textAlign: "center",
          marginTop: 50,
          fontSize: 20,
          color: "#4a90e2",
          fontWeight: "bold",
        }}
      >
        ‚è≥ Loading your profile...
      </p>
    );

  if (error)
    return (
      <p
        style={{
          textAlign: "center",
          marginTop: 50,
          color: "red",
          fontSize: 18,
          fontWeight: "bold",
        }}
      >
        [Error - You need to specify the message]
      </p>
    );

  if (!user) return null;

  return (
    <div
      style={{
        maxWidth: 500,
        margin: "40px auto",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >

      <div
        style={{
          padding: 30,
          borderRadius: 20,
          boxShadow: "0 8px 25px rgba(0,0,0,0.15)",
          background: "linear-gradient(135deg, #f9f9f9, #e0f7fa)",
          textAlign: "center",
          transition: "transform 0.3s",
        }}
        onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
        onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
      >
        <div
          style={{
            width: 120,
            height: 120,
            borderRadius: "50%",
            backgroundColor: "#4a90e2",
            margin: "0 auto 20px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: 48,
            color: "#fff",
            fontWeight: "bold",
            boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
          }}
        >
          {user.name.charAt(0).toUpperCase()}
        </div>
        <h2 style={{ marginBottom: 10, color: "#333", fontSize: 28 }}>
          {user.name} üéâ
        </h2>
        <p style={{ margin: 5, color: "#555", fontSize: 18 }}>
          üìß <strong>Email:</strong> {user.email}
        </p>
        <p style={{ margin: 5, color: "#555", fontSize: 18 }}>
          üõ°Ô∏è <strong>Role:</strong> {user.role}
        </p>
      </div>
    </div>
  );
}
