// src/services/api.js
import axios from "axios";

const BASE_URL = "http://localhost:8080/api";

/* ================== AUTH ================== */
export async function login(email, password) {
  const res = await fetch(`${BASE_URL}/auth/login`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ email, password }),
  });

  if (!res.ok) {
    const body = await res.json().catch(() => ({}));
    throw new Error(body.message || body.error || "Invalid login credentials");
  }

  const data = await res.json(); // { message, userId, email, role, customerId? }

  // ðŸ”¹ If customerId is missing, fetch it from backend
  if (!data.customerId) {
    try {
      const customerRes = await fetch(`${BASE_URL}/customers/user/${data.userId}`);
      if (customerRes.ok) {
        const customerData = await customerRes.json();
        if (customerData.id) {
          data.customerId = customerData.id;
        }
      }
    } catch (err) {
      console.warn("Could not fetch customerId for user", data.userId, err);
    }
  }

  // Save auth info including customerId if found
  saveAuth({
    userId: data.userId,
    customerId: data.customerId, // may still be undefined if not found
    role: data.role,
    email: data.email,
  });

  return data;
}

export async function fetchUsers() {
  return getAllUsers();
}

export async function registerUser(username, email, password, role) {
  const res = await fetch(`${BASE_URL}/auth/register`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      username,
      email,
      password,
      role: role ? role.toUpperCase() : "CUSTOMER",
    }),
  });

  const responseBody = await res.json();
  if (!res.ok) throw new Error(responseBody.message || "Registration failed");

  // Save auth info if registration successful
  if (responseBody.customerId) {
    saveAuth({
      userId: responseBody.userId,
      customerId: responseBody.customerId,
      role: responseBody.role,
      email: responseBody.email,
    });
  }

  return responseBody;
}

/* ================== AUTH HELPERS ================== */
export function saveAuth(data) {
  localStorage.setItem("auth", JSON.stringify(data));
}

export function getAuth() {
  const auth = localStorage.getItem("auth");
  return auth ? JSON.parse(auth) : null;
}

export function clearAuth() {
  localStorage.removeItem("auth");
}

/* ================== CLAIMS ================== */
export async function createClaim(payload) {
  const res = await fetch(`${BASE_URL}/claims`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(payload),
  });
  if (!res.ok) throw new Error("Failed to submit claim");
  return res.json();
}

export async function getAllClaims() {
  const res = await fetch(`${BASE_URL}/claims`);
  if (!res.ok) throw new Error("Failed to fetch claims");
  return res.json();
}

export async function getClaimById(id) {
  const res = await fetch(`${BASE_URL}/claims/${id}`);
  if (!res.ok) throw new Error("Claim not found");
  return res.json();
}

export async function getClaimsByCustomerId(customerId) {
  const res = await fetch(`${BASE_URL}/claims/customer/${customerId}`);
  if (!res.ok) throw new Error("Failed to fetch customer claims");
  return res.json();
}

export async function getClaimsByUserId(userId) {
  const res = await fetch(`${BASE_URL}/claims/user/${userId}`);
  if (!res.ok) throw new Error("Failed to fetch user claims");
  return res.json();
}

export async function updateClaimStatus(id, status) {
  const res = await fetch(`${BASE_URL}/claims/${id}/status`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ status }),
  });
  if (!res.ok) throw new Error("Failed to update claim status");
  return res.json();
}

export async function approveClaim(id) {
  const res = await fetch(`${BASE_URL}/claims/${id}/approve`, { method: "PUT" });
  if (!res.ok) throw new Error("Failed to approve claim");
  return res.json();
}

export async function rejectClaim(id) {
  const res = await fetch(`${BASE_URL}/claims/${id}/reject`, { method: "PUT" });
  if (!res.ok) throw new Error("Failed to reject claim");
  return res.json();
}

// ðŸ”¹ Fully working getMyClaims
export const getMyClaims = async () => {
  const auth = getAuth();

  if (!auth) {
    console.error("User not authenticated");
    return [];
  }

  // Must have customerId to fetch claims safely
  if (!auth.customerId) {
    console.error("No customerId found. Cannot fetch claims.");
    return [];
  }

  try {
    const response = await axios.get(`${BASE_URL}/claims/customer/${auth.customerId}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching claims:", error);
    return [];
  }
};

