// src/components/CustomerStatus.js
import React, { useEffect, useState } from "react";
import "./ClaimList.css";
import { getAuth } from "../utils/util";
import { getClaimsByCustomerId } from "../utils/api";

export default function CustomerStatus() {
  const [claims, setClaims] = useState([]);
  const [filter, setFilter] = useState("ALL");
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [page, setPage] = useState(1);
  const pageSize = 5;

  // ✅ Fetch only logged-in customer's claims
  useEffect(() => {
    async function fetchClaims() {
      try {
        const auth = getAuth();
        if (!auth || !auth.userId) throw new Error("Not logged in");

        const customerClaims = await getClaimsByCustomerId(auth.userId);
        setClaims(customerClaims);
      } catch (err) {
        console.error("Failed to fetch customer claims", err);
        setClaims([]);
      }
    }

    fetchClaims();
  }, []);

  // Sorting
  const handleSort = (field) => {
    const order = sortField === field && sortOrder === "asc" ? "desc" : "asc";
    setSortField(field);
    setSortOrder(order);
  };

  // Filter + sort
  const sortedClaims = [...claims]
    .filter((c) => (filter === "ALL" ? true : c.status === filter))
    .sort((a, b) => {
      if (!sortField) return 0;
      const valA = a[sortField];
      const valB = b[sortField];
      if (valA < valB) return sortOrder === "asc" ? -1 : 1;
      if (valA > valB) return sortOrder === "asc" ? 1 : -1;
      return 0;
    });

  // Pagination
  const paginatedClaims = sortedClaims.slice(
    (page - 1) * pageSize,
    page * pageSize
  );

  const getCustomerName = (claim) => claim.customer?.name || "Unknown";

  return (
    <div className="claim-list">
      <h2>My Claims</h2>

      {/* Status Filter */}
      <div className="filter-bar">
        <label>Status:</label>
        <select
          value={filter}
          onChange={(e) => {
            setFilter(e.target.value);
            setPage(1); // reset pagination on filter change
          }}
        >
          <option value="ALL">All</option>
          <option value="SUBMITTED">Submitted</option>
          <option value="UNDER_REVIEW">Under Review</option>
          <option value="APPROVED">Approved</option>
          <option value="REJECTED">Rejected</option>
        </select>
      </div>

      {paginatedClaims.length === 0 ? (
        <p>No claims found.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th onClick={() => handleSort("id")}>
                ID {sortField === "id" ? (sortOrder === "asc" ? "↑" : "↓") : ""}
              </th>
              <th>Customer</th>
              <th onClick={() => handleSort("claimType")}>
                Type{" "}
                {sortField === "claimType"
                  ? sortOrder === "asc"
                    ? "↑"
                    : "↓"
                  : ""}
              </th>
              <th onClick={() => handleSort("claimAmount")}>
                Amount{" "}
                {sortField === "claimAmount"
                  ? sortOrder === "asc"
                    ? "↑"
                    : "↓"
                  : ""}
              </th>
              <th onClick={() => handleSort("status")}>
                Status{" "}
                {sortField === "status"
                  ? sortOrder === "asc"
                    ? "↑"
                    : "↓"
                  : ""}
              </th>
              <th onClick={() => handleSort("submissionDate")}>
                Submitted{" "}
                {sortField === "submissionDate"
                  ? sortOrder === "asc"
                    ? "↑"
                    : "↓"
                  : ""}
              </th>
              <th>File</th>
            </tr>
          </thead>
          <tbody>
            {paginatedClaims.map((c) => (
              <tr key={c.id}>
                <td>{c.id}</td>
                <td>{getCustomerName(c)}</td>
                <td>{c.claimType}</td>
                <td>{c.claimAmount?.toFixed(2)}</td>
                <td>{c.status}</td>
                <td>{c.submissionDate}</td>
                <td>
                  {c.fileUrl ? (
                    <a href={c.fileUrl} target="_blank" rel="noopener noreferrer">
                      View File
                    </a>
                  ) : (
                    <span className="text-muted">No file</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Pagination */}
      {paginatedClaims.length > 0 && (
        <div className="pagination">
          <button
            onClick={() => setPage((p) => Math.max(1, p - 1))}
            disabled={page === 1}
          >
            Prev
          </button>
          <span>
            Page {page} of {Math.ceil(sortedClaims.length / pageSize)}
          </span>
          <button
            onClick={() =>
              setPage((p) => (p * pageSize < sortedClaims.length ? p + 1 : p))
            }
            disabled={page * pageSize >= sortedClaims.length}
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}
