// src/components/CustomerList.jsx
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import * as CustomerService from "../utils/api";
import "./CustomerList.css";

export default function CustomerList() {
  const [customers, setCustomers] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetchRegisteredCustomers();
  }, []);

  const fetchRegisteredCustomers = async () => {
    setLoading(true);
    try {
      const data = await CustomerService.getAllCustomers();
      // Only show customers that came from registration page
      // (in your case, all customers from DB are registered ones)
      setCustomers(data);
    } catch (err) {
      setError("Failed to fetch registered customers");
    } finally {
      setLoading(false);
    }
  };

  const filtered = customers.filter(c =>
    c.policyNumber?.toLowerCase().includes(search.toLowerCase())
  );

  if (loading)
    return (
      <div className="text-center mt-5">
        <div className="spinner-border text-primary" role="status"></div>
        <p className="mt-3">Loading registered customers...</p>
      </div>
    );

  if (error)
    return (
      <div className="alert alert-danger text-center mt-5">{error}</div>
    );

  if (filtered.length === 0)
    return (
      <div className="container mt-5">
        <div className="alert alert-info text-center">
          No registered customers found.
        </div>
      </div>
    );

  return (
    <div className="container mt-5">
      <h3 className="text-center text-primary fw-bold mb-4">
        Registered Customer List
      </h3>

      <div className="d-flex justify-content-center mb-3">
        <input
          type="text"
          placeholder="Search by policy number..."
          className="form-control w-50"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      <div className="row">
        {filtered.map((c) => (
          <div key={c.customerId} className="col-md-4 mb-4">
            <div className="card shadow-sm h-100 border-0">
              <div className="card-body text-center">
                <div
                  className="rounded-circle bg-primary text-white mx-auto mb-3"
                  style={{
                    width: 60,
                    height: 60,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontSize: 22,
                    fontWeight: "bold",
                  }}
                >
                  {c.name?.charAt(0).toUpperCase()}
                </div>
                <h5>{c.name}</h5>
                <p className="text-muted mb-1">{c.email}</p>
                <p className="text-secondary mb-1">
                  üìû {c.phone || "N/A"}
                </p>
                <p className="text-secondary">
                  üìù {c.policyNumber || "N/A"}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
