import React, { useState, useEffect } from "react";
import { getMyClaims } from "../utils/api";
import "./ClaimList.css";

export default function CustomerStatus() {
  const [claims, setClaims] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchClaims = async () => {
    setLoading(true);
    setError("");

    try {
      const data = await getMyClaims();
      setClaims(data);
    } catch (err) {
      console.error(err);
      setError(err.message || "Failed to load claims. Please try again.");
      setClaims([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchClaims();
    const interval = setInterval(fetchClaims, 5000); // auto-refresh every 5 sec
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="claim-list">
      <h2>My Claims</h2>

      {loading ? (
        <p>Loading claims...</p>
      ) : error ? (
        <p style={{ color: "red" }}>{error}</p>
      ) : claims.length === 0 ? (
        <p>No claims found.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Amount</th>
              <th>Type</th>
              <th>Description</th>
              <th>Status</th>
              <th>Submission Date</th>
              <th>File</th>
            </tr>
          </thead>
          <tbody>
            {claims.map((c) => (
              <tr key={c.id}>
                <td>{c.id}</td>
                <td>{c.claimAmount}</td>
                <td>{c.claimType}</td>
                <td>{c.claimDescription}</td>
                <td>{c.status}</td>
                <td>{new Date(c.submissionDate).toLocaleDateString()}</td>
                <td>
                  {c.fileUrl ? (
                    <a href={c.fileUrl} target="_blank" rel="noopener noreferrer">
                      View
                    </a>
                  ) : (
                    "No file"
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
