// src/services/api.js
import axios from "axios"; // Make sure axios is imported

const BASE_URL = "http://localhost:8080/api";

/* ================== AUTH ================== */
export async function login(email, password) {
  const res = await fetch(`${BASE_URL}/auth/login`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ email, password }),
  });

  if (!res.ok) {
    const body = await res.json().catch(() => ({}));
    throw new Error(body.message || body.error || "Invalid login credentials");
  }

  return res.json(); // { message, userId, email, role, customerId? }
}

export async function fetchUsers() {
  return getAllUsers();
}

export async function registerUser(username, email, password, role) {
  const res = await fetch(`${BASE_URL}/auth/register`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      username,
      email,
      password,
      role: role ? role.toUpperCase() : "CUSTOMER",
    }),
  });

  const responseBody = await res.json();
  if (!res.ok) throw new Error(responseBody.message || "Registration failed");

  // Save auth info if registration successful
  if (responseBody.customerId) {
    saveAuth({
      userId: responseBody.userId,
      customerId: responseBody.customerId,
      role: responseBody.role,
      email: responseBody.email,
    });
  }

  return responseBody;
}

/* ================== AUTH HELPERS ================== */
export function saveAuth(data) {
  localStorage.setItem("auth", JSON.stringify(data));
}

export function getAuth() {
  const auth = localStorage.getItem("auth");
  return auth ? JSON.parse(auth) : null;
