import React, { useState } from "react";
import { login, saveAuth } from "../utils/api";
import "./Login.css";

export default function Login({ navigate, setRole }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      // Call login API
      const data = await login(email, password);

      // Save all auth info including customerId
      const authData = {
        role: data.role.toLowerCase(),
        email: data.email,
        userId: data.userId,
        customerId: data.customerId || null, // ensures CustomerStatus can fetch claims
      };

      saveAuth(authData); // Save to localStorage
      setRole(authData.role);

      // Navigate to home page after login
      navigate("home", null, authData.role);
    } catch (err) {
      setError(err.message || "Login failed. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2 className="login-title">Welcome Back ðŸ‘‹</h2>
        <p className="login-subtitle">Login to continue to InsuraCare</p>

        {error && <p className="error">{error}</p>}

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="btn-login">Login</button>
        </form>

        <p className="redirect-text">
          Donâ€™t have an account?{" "}
          <span className="link" onClick={() => navigate("register")}>
            Register
          </span>
        </p>
      </div>
    </div>
  );
}
